import math
import random


class plsa():

	## コンストラクタ
	def __init__(self,n,nz=5):
		""" n:dw行列 nz:潜在変数(トピック)の数""" # d:文書 w:単語 nz:トピック数
		self.n = n  # インスタンス変数n 入力行列
		self.nz = nz # トピック数(現在は2)
		self.nd = len(n) # 文書数(現在は3)
		self.nw = len(n[0]) # 単語数(現在は4)

		####### print self.n
		####### print self.nd, self.nw, self.nz

		"""各確率を初期化""" # 関数arrで多次元配列を生成する
		# [a, b]が渡されたら a行b列の多次元配列生成
		# print "*************** self.pdw_z ********************"
		# print [self.nd, self.nw, self.nz] 
		self.pdw_z = self.arr([ self.nd, self.nw, self.nz ]) # P(z|d,w)
		# print self.pdw_z

		# print "*************** self.pz_w ********************"
		# print [self.nz, self.nw]
		self.pz_w = self.arr([ self.nz, self.nw ]) # P(w|z)
		# print self.pz_w

		# print "*************** self.pz_d ********************"
		# print [self.nz, self.nd]
		self.pz_d = self.arr([ self.nz, self.nd ]) # P(d|z)
		# print self.pz_d

		# print "*************** self.pz ********************"
		# print [self.nz]
		self.pz = self.arr([ self.nz ]) # P(z)
		# print self.pz

		# print "*************** self.pdw ********************"
		# print [self.nd, self.nw]
		self.pdw = self.arr([ self.nd, self.nw ]) # P(d,w)
		# print self.pdw

		####### print "\n"

		# print "P(z) = ",
		### print self.pz # P(z) 確認用
		# print "P(d|z) = ",
		### print self.pz_d # P(d|z) 確認用
		# print "P(w|z) = ",
		### print self.pz_w # P(w|z) 確認用
		# print "P(z|d,w) = ",
		### print self.pdw_z # P(z|d,w) 確認用
		# print "P(d,w) = ",
		### print self.pdw # P(d,w) 最終的な出力ではこの項はなかった

		####### print "\n"


	def train(self,k=1000):
		# 収束するまで繰り返す
		tmp = 0
		for i in xrange(k):
			# print ""
			# print str(i) + "回目" # 数値を文字列に変換することによって同時に出力できる
			# print "*************** e_step ***************"
			self.e_step()
			# print "*************** m_step ***************"
			self.m_step()
			# print "*************** likelihood ***************"
			L = self.likelihood()
			# print abs(L - tmp) ## 確認用
			# 収束したか
			if abs(L - tmp) < 1.0e-10:
				break
			else:
				tmp = L
		####### print str(i) + "回目で終了" 


	# map()はリストやタプルの各要素に対して同じ処理を施して，同じ長さのリストにそのすべての結果を格納してくれる関数 map(関数, リスト)
	# lambdaは無名関数 この場合 aに対して a/total を行う関数を表す
	@staticmethod
	def normalized(list):
		# print "normalized"
		""" 合計が1になるように正規化する """
		total = sum(list)
		return map(lambda a: a/total, list)


	# 渡されたリスト長が1より大きければ，0番目(list[0])の値分だけ，arr関数を回す
	# このとき，1番目以降の値(list[1:])のみをリストとして渡している．それを繰り返して新たな多次元配列を生成
	# 渡されたリスト長が1ならば，その値(list[0])分だけ乱数を発生させ，新たなリストを生成し正規化（合計を1に）する
	# list = [2] であった場合，[0.246466, 0.6343225]のように2つの要素があるリストが生成
	@staticmethod
	def arr(list):
		""" 多次元配列を生成する
		list=[M,N...] としたら M x N x... で要素がランダムの配列を返す """
		if len(list) > 1:
			# print "if"
			return [ plsa.arr(list[1:]) for i in xrange(list[0]) ]
		else:
			# print "else"
			return plsa.normalized([ random.random() for i in xrange(list[0]) ])



	def likelihood(self):
		""" 確率尤度関数の計算 """
		# P(d,w)
		for d in xrange(self.nd):
			for w in xrange(self.nw):
				self.pdw[d][w] = sum([ self.pz[z] * self.pz_d[z][d] * self.pz_w[z][w] for z in xrange(self.nz) ]) # P(d,w) = Σz{P(z) * P(d|z) * P(w|z)}

		# for w in xrange(self.nw):
		# 	for d in xrange(self.nd):
		# 		print self.pdw[d][w]
		# 		math.log(self.pdw[d][w])		

		# Σ n(d,w) log P(d,w)
		# print self.n[d][w], math.log(self.pdw[d][w]), self.nd, self.nw # 確認用
		
		# self.pdw[d][w]で 0 が入っていた場合は無視するようにif文を入れる(sumだから関係ない)
		return sum([ self.n[d][w] * math.log(self.pdw[d][w]) for d in xrange(self.nd) for w in xrange(self.nw) if self.pdw[d][w] != 0]) # Σd,w{n(d,w) * log(P(d,w))}


	def e_step(self):
		""" Eステップ """
		# P(z|d,w)
		for d in xrange(self.nd):
			for w in xrange(self.nw):
				for z in xrange(self.nz):
					self.pdw_z[d][w][z] = self.pz[z] * self.pz_d[z][d] * self.pz_w[z][w] # P(z|d,w) = P(z) * P(d|z) * P(w|z)
				self.pdw_z[d][w] = self.normalized(self.pdw_z[d][w]) # 正規化


	def m_step(self):
		""" Mステップ """
		# P(w|z)
		for z in xrange(self.nz):
			for w in xrange(self.nw):
				self.pz_w[z][w] = sum([ self.n[d][w] * self.pdw_z[d][w][z] for d in xrange(self.nd) ]) # P(w|z) = Σd{n(d,w) * P(z|d,w)}    n(d,w):単語wが文書dに出現する回数
			self.pz_w[z] = self.normalized(self.pz_w[z]) # 正規化

		# P(d|z)
		for z in xrange(self.nz):
			for d in xrange(self.nd):
				self.pz_d[z][d] = sum([ self.n[d][w] * self.pdw_z[d][w][z] for w in xrange(self.nw) ]) # P(d|z) = Σw{n(d,w) * P(z|d,w)}
			self.pz_d[z] = self.normalized(self.pz_d[z]) # 正規化

		# P(z)
		for z in xrange(self.nz):
			self.pz[z] = sum([ self.n[d][w] * self.pdw_z[d][w][z] for d in xrange(self.nd) for w in xrange(self.nw) ]) # P(z) = Σd,w{n(d,w) * P(z|d,w)}
		self.pz = self.normalized(self.pz)



